package com.noahcharlton.robogeddon.client;import com.badlogic.gdx.Files;import com.badlogic.gdx.backends.lwjgl.LwjglApplicationConfiguration;import com.noahcharlton.robogeddon.Core;import com.noahcharlton.robogeddon.Log;import com.noahcharlton.robogeddon.client.watchdog.Watchdog;import com.noahcharlton.robogeddon.client.watchdog.WatchdogApplication;import com.noahcharlton.robogeddon.settings.ClientSettings;import java.util.Arrays;public class ClientLauncher {	public static void main (String[] args) {		Log.info("Client Launcher started!");		Log.info("Core Version: " + Core.VERSION + "-" + Core.VERSION_TYPE);		Log.info("Args: " + Arrays.toString(args));		Watchdog.watch(Thread.currentThread());		ClientSettings.init();		Core.client = GameClient.getInstance();		Core.preInit();		startLwjgl();	}	private static void startLwjgl() {		LwjglApplicationConfiguration config = new LwjglApplicationConfiguration();		config.forceExit = false;		config.foregroundFPS = 0;		config.width = ClientSettings.windowWidth.getValue();		config.height = ClientSettings.windowHeight.getValue();		config.backgroundFPS = 120;		config.vSyncEnabled = ClientSettings.vSync.getValue();		config.title = "Robogeddon " + Core.VERSION + "-" + Core.VERSION_TYPE;		config.addIcon("icons/icon128.png", Files.FileType.Internal);		config.addIcon("icons/icon64.png", Files.FileType.Internal);		config.addIcon("icons/icon32.png", Files.FileType.Internal);		new WatchdogApplication(GameClient.getInstance(), config);	}}