package com.noahcharlton.robogeddon.ui.ingame;import com.noahcharlton.robogeddon.Core;import com.noahcharlton.robogeddon.ui.UIAssets;import com.noahcharlton.robogeddon.ui.widget.*;import com.noahcharlton.robogeddon.world.item.Item;public class InventoryList extends Stack {    public InventoryList() {        for(Item item: Core.items.values()){            var texture = new TextureWidget().setTexture(item.getTexture());            var label = new Label().setSupplier(() -> getText(item)).setFont(UIAssets.smallFont).pad().bottom(10);            var pair = new PairLayout().setLeft(texture).setRight(label).setUserObject(item);            add(pair);        }    }    private String getText(Item item) {        int amount = 0;        if(client.getWorld() != null){            amount = client.getWorld().getInventoryForItem(item);        }        return item.getDisplayName() + ": " + amount;    }    @Override    public void layout() {        super.layout();    }    @Override    public void update() {        for(Widget child : getChildren()){            var item = (Item) child.getUserObject();            child.setVisible(client.getWorld().getInventoryForItem(item) > 0);        }    }}